// Program to display a right angle triangle using asterisks
public class Asterisk_Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 int rows = 4;

	        for (int i = 1; i <= rows; i++) {

	            for (int j = 1; j <= i; j++) {
	                System.out.print("*");
	            }

	            System.out.println();
	        }

	}

}
